mapper.select:
SELECT id, board_name, subject, title, author, write_time, content
FROM article
WHERE id = #{id};

SELECT id, board_name, subject, title, author, write_time, content
FROM article
WHERE board_name = #{boardName}
ORDER BY write_time desc
LIMIT #{offset}, #{articlesPerPage};

mapper.update:
UPDATE article
SET board_name = #{board_name},
    title = #{title},
    subject = #{subject},
    content = #{content}
WHERE id = #{id};
