mapper.select:
SELECT id, pw, name, tel,
       bmk_addr_lv1, bmk_addr_lv2, bmk_addr_lv3, bmk_addr_lv4, role
FROM user;

mapper.insert:
INSERT INTO user (id, pw, name, tel, bmk_addr_lv1, bmk_addr_lv2, bmk_addr_lv3, bmk_addr_lv4, role)
VALUES (#{id}, #{pw}, #{name}, #{tel},
        #{bmk_addr_lv1}, #{bmk_addr_lv2}, #{bmk_addr_lv3}, #{bmk_addr_lv4}, #{role});
